#include <iostream>
#include <vector>
#include <algorithm>

struct Train {
    int cars, depart, arrive;
};

bool compareTrains(const Train& a, const Train& b) {
    return a.arrive < b.arrive;
}

int main() {
    int NAB, NBA;
    std::cin >> NAB >> NBA;

    std::vector<Train> trainsA(NAB), trainsB(NBA);

    for (int i = 0; i < NAB; i++) {
        std::cin >> trainsA[i].cars >> trainsA[i].depart >> trainsA[i].arrive;
    }

    for (int i = 0; i < NBA; i++) {
        std::cin >> trainsB[i].cars >> trainsB[i].depart >> trainsB[i].arrive;
    }

    std::sort(trainsA.begin(), trainsA.end(), compareTrains);
    std::sort(trainsB.begin(), trainsB.end(), compareTrains);

    int maxUnits = 0;
    int i = 0, j = 0;

    while (i < NAB && j < NBA) {
        if (trainsA[i].arrive <= trainsB[j].depart) {
            maxUnits = std::max(maxUnits, trainsA[i].cars + trainsB[j].cars);
            i++;
        } else {
            j++;
        }
    }

    std::cout << maxUnits << std::endl;

    return 0;
}
