import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int NAB = scanner.nextInt();
        int NBA = scanner.nextInt();
        int[][] A = new int[NAB][3];
        int[][] B = new int[NBA][3];
        for (int i = 0; i < NAB; i++) {
            A[i][0] = scanner.nextInt();
            A[i][1] = scanner.nextInt();
            A[i][2] = scanner.nextInt();
        }
        for (int i = 0; i < NBA; i++) {
            B[i][0] = scanner.nextInt();
            B[i][1] = scanner.nextInt();
            B[i][2] = scanner.nextInt();
        }
        Arrays.sort(A, Comparator.comparingInt(a -> a[1]));
        Arrays.sort(B, Comparator.comparingInt(a -> a[1]));
        int i = 0, j = 0;
        int max = 0;
        while (i < NAB && j < NBA) {
            if (A[i][1] <= B[j][1]) {
                int min = Math.min(A[i][2], B[j][2]);
                int max1 = Math.max(A[i][1], B[j][1]);
                int cars = A[i][0] + B[j][0];
                int time = min - max1;
                if (time >= 0) {
                    max = Math.max(max, cars);
                }
                i++;
            } else {
                j++;
            }
        }
        System.out.println(max);
    }
}
