#include <stdio.h>
#include <stdlib.h>

typedef struct Point {
    int x, y;
} Point;

int compare(const void *a, const void *b) {
    Point *p1 = (Point *)a;
    Point *p2 = (Point *)b;
    if (p1->x == p2->x) {
        return p1->y - p2->y;
    }
    return p1->x - p2->x;
}

int main() {
    int n;
    scanf("%d", &n);
    Point *points = (Point *)malloc(n * sizeof(Point));
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &points[i].x, &points[i].y);
    }
    qsort(points, n, sizeof(Point), compare);
    int total_length = 0;
    for (int i = 0; i < n - 1; i++) {
        total_length += abs(points[i].x - points[i + 1].x) + abs(points[i].y - points[i + 1].y);
    }
    printf("%d\n", total_length);
    free(points);
    return 0;
}
