#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 1005
#define MAXM 10005

typedef struct Edge {
    int u, v, w;
} Edge;

int n, m;
Edge e[MAXM];
int fa[MAXN];

int find(int x) {
    if (fa[x] != x) fa[x] = find(fa[x]);
    return fa[x];
}

int kruskal() {
    int i, j, cnt = 0, sum = 0;
    for (i = 1; i <= n; i++) fa[i] = i;
    qsort(e, m, sizeof(Edge), cmp);
    for (i = 0; i < m; i++) {
        int u = find(e[i].u), v = find(e[i].v);
        if (u != v) {
            fa[u] = v;
            cnt++;
            sum += e[i].w;
            if (cnt == n - 1) break;
        }
    }
    return sum / (n - 1);
}

int cmp(const void *a, const void *b) {
    Edge *x = (Edge *)a, *y = (Edge *)b;
    return x->w - y->w;
}

int main() {
    while (scanf("%d%d", &n, &m) == 2 && n && m) {
        int i;
        for (i = 0; i < m; i++) scanf("%d%d%d", &e[i].u, &e[i].v, &e[i].w);
        printf("%d\n", kruskal());
    }
    return 0;
}
