#include <bits/stdc++.h>
using namespace std;

const int MAXN = 1005;
const int INF = 1e9;

struct Edge {
    int u, v, w;
};

int n, m;
vector<Edge> edges;
vector<int> parent;
vector<int> rank;
vector<int> size;
vector<int> cost;

int find(int u) {
    if (u == parent[u])
        return u;
    return parent[u] = find(parent[u]);
}

void unionSet(int u, int v) {
    u = find(u);
    v = find(v);
    if (u == v)
        return;
    if (rank[u] < rank[v])
        swap(u, v);
    parent[v] = u;
    if (rank[u] == rank[v])
        rank[u]++;
    size[u] += size[v];
}

int getCost(int u) {
    if (u == parent[u])
        return cost[u];
    return cost[u] = getCost(parent[u]);
}

int main() {
    while (cin >> n >> m) {
        if (n == 0 && m == 0)
            break;
        edges.clear();
        parent.resize(n + 1);
        rank.resize(n + 1);
        size.resize(n + 1);
        cost.resize(n + 1);
        for (int i = 1; i <= n; i++) {
            parent[i] = i;
            rank[i] = 1;
            size[i] = 1;
            cost[i] = 0;
        }
        for (int i = 0; i < m; i++) {
            int u, v, w;
            cin >> u >> v >> w;
            edges.push_back({u, v, w});
        }
        sort(edges.begin(), edges.end(), [](const Edge& a, const Edge& b) {
            return a.w < b.w;
        });
        int totalCost = 0;
        for (const auto& edge : edges) {
            int u = edge.u;
            int v = edge.v;
            int w = edge.w;
            if (find(u) != find(v)) {
                totalCost += w;
                cost[find(u)] = w;
                unionSet(u, v);
            }
        }
        int median = (n / 2) * 2;
        int sum = 0;
        for (int i = 1; i <= n; i++) {
            sum += getCost(i);
        }
        cout << (sum - totalCost) / (n - 1) << endl;
    }
    return 0;
}
