using namespace std;

struct Ball {
    double x, y, vx, vy, r;
};

bool checkCollision(Ball& ball1, Ball& ball2) {
    double dx = ball2.x - ball1.x;
    double dy = ball2.y - ball1.y;
    double distance = sqrt(dx * dx + dy * dy);
    return distance < ball1.r + ball2.r;
}

void simulate(Ball& ball, vector<Ball>& balls, int& hitIndex, int& stopIndex) {
    for (int i = 0; i < balls.size(); i++) {
        if (i == 0) continue;
        if (checkCollision(ball, balls[i])) {
            hitIndex = i;
            return;
        }
    }
    stopIndex = 1;
}

