import math

def distance(x1, y1, x2, y2):
    return math.sqrt((x1 - x2) ** 2 + (y1 - y2) ** 2)

def collision(x, y, vx, vy, r, w, h, balls):
    for i in range(len(balls)):
        if i != 0:
            bx, by = balls[i]
            if distance(x, y, bx, by) < 2 * r:
                return i
    return -1

def simulate(x, y, vx, vy, r, w, h, balls):
    total_distance = 0
    while total_distance < 10000:
        collision_index = collision(x, y, vx, vy, r, w, h, balls)
        if collision_index != -1:
            return collision_index
        total_distance += math.sqrt(vx ** 2 + vy ** 2)
        x += vx
        y += vy
        if x - r < 0 or x + r > w:
            vx = -vx
        if y - r < 0 or y + r > h:
            vy = -vy
    return -1

def main():
    while True:
        n = int(input())
        if n == 0:
            break
        w, h, r, vx, vy = map(int, input().split())
        balls = []
        for _ in range(n):
            x, y = map(int, input().split())
            balls.append((x, y))
        result = simulate(balls[0][0], balls[0][1], vx, vy, r, w, h, balls)
        print(result)

if __name__ == "__main__":
    main()
