#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_H 55
#define MAX_W 55
#define MAX_C 55

int h, w;
char c[MAX_H][MAX_W + 1];
int dp[MAX_H][MAX_W];
int bag[MAX_C];
int cnt;

void dfs(int x, int y) {
    if (x == h && y == w) {
        cnt = 0;
        for (int i = 0; i < cnt; i++) {
            if (bag[cnt - 1 - i] == c[x][y]) {
                cnt = i;
                break;
            }
        }
        return;
    }
    if (x > h || y > w || c[x][y] == '#') {
        return;
    }
    if (dp[x][y] != -1) {
        dfs(x + 1, y);
        dfs(x, y + 1);
        return;
    }
    dp[x][y] = -1;
    if (c[x][y] >= 'a' && c[x][y] <= 'z') {
        bag[cnt++] = c[x][y];
        dfs(x + 1, y);
        dfs(x, y + 1);
        cnt--;
    } else if (c[x][y] >= 'A' && c[x][y] <= 'Z') {
        for (int i = cnt - 1; i >= 0; i--) {
            if (bag[i] == c[x][y]) {
                dfs(x + 1, y);
                dfs(x, y + 1);
                break;
            }
        }
    } else {
        dfs(x + 1, y);
        dfs(x, y + 1);
    }
}

int main() {
    int t;
    scanf("%d", &t);
    while (t--) {
        scanf("%d %d", &h, &w);
        for (int i = 0; i < h; i++) {
            scanf("%s", c[i]);
        }
        memset(dp, -1, sizeof(dp));
        cnt = 0;
        dfs(1, 1);
        printf("%d\n", cnt);
    }
    return 0;
}
