#include <iostream>
#include <vector>
#include <string>
#include <stack>
#include <unordered_map>

using namespace std;

int solve(vector<string>& grid, int h, int w) {
    unordered_map<char, int> jewelCount;
    stack<char> jewels;
    int res = 0;

    for (int i = h - 2; i >= 0; i--) {
        for (int j = w - 2; j >= 0; j--) {
            if (grid[i][j] >= 'a' && grid[i][j] <= 'z') {
                jewelCount[grid[i][j]]++;
            } else if (grid[i][j] >= 'A' && grid[i][j] <= 'Z') {
                if (!jewels.empty() && jewels.top() == toupper(grid[i][j])) {
                    jewels.pop();
                    res++;
                }
            } else if (grid[i][j] == '.') {
                if (!jewels.empty()) {
                    jewels.push(grid[i][j]);
                }
            }
        }
    }

    return res;
}

int main() {
    int h, w;
    while (cin >> h >> w) {
        if (h == 0 && w == 0) break;
        vector<string> grid(h);
        for (int i = 0; i < h; i++) {
            cin >> grid[i];
        }
        cout << solve(grid, h, w) << endl;
    }
    return 0;
}
