import java.util.Scanner;
import java.util.Stack;

public class Main {
    static int[][] maze;
    static int h, w;
    static Stack<Character> bag = new Stack<>();

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            h = scanner.nextInt();
            w = scanner.nextInt();
            if (h == 0 && w == 0) break;
            maze = new int[h + 1][w + 1];
            for (int i = 1; i <= h; i++) {
                String s = scanner.next();
                for (int j = 1; j <= w; j++) {
                    char c = s.charAt(j - 1);
                    if (c >= 'a' && c <= 'z') {
                        maze[i][j] = 1;
                    } else if (c >= 'A' && c <= 'Z') {
                        maze[i][j] = -1;
                    } else if (c == '#') {
                        maze[i][j] = -2;
                    }
                }
            }
            System.out.println(dfs(1, 1));
        }
    }

    static int dfs(int x, int y) {
        if (x == h && y == w) return 0;
        if (maze[x][y] == -2) return -1;
        if (maze[x][y] == -1) {
            if (bag.isEmpty()) return -1;
            char c = bag.pop();
            if (c == (char) (maze[x][y] + 97)) return 1 + dfs(x, y + 1);
            return -1;
        }
        if (maze[x][y] == 1) {
            bag.push((char) (maze[x][y] + 64));
            return 1 + dfs(x + 1, y);
        }
        return Math.max(dfs(x + 1, y), dfs(x, y + 1));
    }
}
