from collections import deque

def solve(maze):
    h, w = len(maze), len(maze[0])
    visited = [[False]*w for _ in range(h)]
    queue = deque([(0, 0, 0, set())])
    visited[0][0] = True

    while queue:
        x, y, count, jewels = queue.popleft()
        if x == h-1 and y == w-1:
            return count

        for dx, dy in [(0, 1), (1, 0)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < h and 0 <= ny < w and not visited[nx][ny]:
                if maze[nx][ny] != '#':
                    queue.append((nx, ny, count, jewels))
                    visited[nx][ny] = True
                elif maze[nx][ny].islower():
                    if maze[nx][ny] in jewels:
                        queue.append((nx, ny, count+1, jewels))
                        visited[nx][ny] = True
                elif maze[nx][ny].isupper():
                    if maze[nx][ny].lower() in jewels:
                        queue.append((nx, ny, count+1, jewels))
                        visited[nx][ny] = True

    return -1

def main():
    while True:
        h, w = map(int, input().split())
        if h == 0 and w == 0:
            break
        maze = [list(input()) for _ in range(h)]
        print(solve(maze))

if __name__ == "__main__":
    main()
