#include <iostream>
#include <vector>
#include <cstdint>

int64_t lowbit(int64_t x) {
    return x & -x;
}

int64_t highbit(int64_t x) {
    return x == 0 ? 0 : (x & -x) * 2;
}

int64_t count(int64_t x) {
    int64_t res = 0;
    while (x) {
        res++;
        x >>= 1;
    }
    return res;
}

int64_t solve(std::vector<int64_t>& k, int64_t n) {
    int64_t A = 1;
    int64_t B = 1;
    for (int i = 0; i < n; i++) {
        if (k[i] == 0) {
            continue;
        }
        int64_t l = lowbit(B);
        int64_t h = highbit(B);
        if (k[i] > count(B) - count(A)) {
            return -1;
        }
        if (k[i] == count(B) - count(A)) {
            if (i == n - 1) {
                return A;
            }
            continue;
        }
        if (k[i] < count(B) - count(A)) {
            int64_t t = count(B) - count(A) - k[i];
            if (t == 0) {
                A = B + 1;
                continue;
            }
            if (t & 1) {
                return -1;
            }
            int64_t p = lowbit(t / 2);
            int64_t q = highbit(t / 2);
            if (p > l) {
                return -1;
            }
            if (q < h) {
                return -1;
            }
            A = B + p;
            B = B + q;
        }
    }
    return A;
}

int main() {
    int64_t n;
    while (std::cin >> n) {
        if (n == 0) {
            break;
        }
        std::vector<int64_t> k(n);
        for (int i = 0; i < n; i++) {
            std::cin >> k[i];
        }
        int64_t res = solve(k, n);
        if (res == -1) {
            std::cout << "None" << std::endl;
        } else if (res == -2) {
            std::cout << "Many" << std::endl;
        } else {
            std::cout << res << " " << res << std::endl;
        }
    }
    return 0;
}
