import java.util.Scanner;

public class DebtHell {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        double debt = 100000;
        for (int i = 0; i < n; i++) {
            debt = Math.round(debt * 1.05) / 1;
        }
        System.out.println((int) debt);
    }
}
