int min(int a, int b) {
    return a < b ? a : b;
}

int max(int a, int b) {
    return a > b ? a : b;
}

int dijkstra(int n, int s, int t, int** d, int** p, int** w) {
    int i, j, k, u, v, w_;
    int* dist = (int*)malloc((n + 1) * sizeof(int));
    int* prev = (int*)malloc((n + 1) * sizeof(int));
    int* q = (int*)malloc((n + 1) * sizeof(int));
    int cnt = 0;

    for (i = 1; i <= n; i++) {
        dist[i] = INF;
        prev[i] = -1;
    }

    dist[s] = 0;
    q[cnt++] = s;

    while (cnt > 0) {
        u = q[cnt - 1];
        cnt--;

        for (i = 0; i < n; i++) {
            if (w[u][i] != INF) {
                v = i + 1;
                w_ = w[u][v];

                if (w_ != -1) {
                    w_[0] = min(w_[0], dist[u] + 1);
                } else {
                    w_[0] = min(w_[0], dist[u] + 1);
                }

                if (dist[v] > w_[0]) {
                    dist[v] = w_[0];
                    prev[v] = u;
                    q[cnt++] = v;
                }
            }
        }
    }

    if (dist[t] == INF) {
        return -1;
    }

    for (i = 1; i <= n; i++) {
        if (dist[i] < INF) {
            d[i][0] = dist[i];
            p[i][0] = prev[i];
        }
    }

    return 0;
}

