import java.io.*;
import java.util.*;

public class Main {
    static final int N = 1005;
    static int n, m, s1, s2, t;
    static int[] head = new int[N];
    static int[] to = new int[N * 2];
    static int[] nxt = new int[N * 2];
    static int[] w = new int[N * 2];
    static int idx;
    static int[] dis = new int[N];
    static int[] vis = new int[N];
    static int[] pre = new int[N];

    static void add(int u, int v, int c) {
        nxt[idx] = head[u];
        to[idx] = v;
        w[idx] = c;
        head[u] = idx++;
    }

    static void dij(int s) {
        Arrays.fill(dis, Integer.MAX_VALUE);
        Arrays.fill(vis, 0);
        PriorityQueue<int[]> pq = new PriorityQueue<>((a, b) -> a[0] - b[0]);
        dis[s] = 0;
        pq.offer(new int[]{0, s});
        while (!pq.isEmpty()) {
            int[] cur = pq.poll();
            int u = cur[1];
            if (vis[u] == 1) continue;
            vis[u] = 1;
            for (int i = head[u]; i != -1; i = nxt[i]) {
                int v = to[i];
                int c = w[i];
                if (c > 0) {
                    if (dis[v] > dis[u] + c) {
                        dis[v] = dis[u] + c;
                        pre[v] = u;
                        pq.offer(new int[]{dis[v], v});
                    }
                }
            }
        }
    }

    static int get(int x) {
        return x == pre[x] ? x : (pre[x] = get(pre[x]));
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        while (true) {
            n = sc.nextInt();
            m = sc.nextInt();
            s1 = sc.nextInt();
            s2 = sc.nextInt();
            t = sc.nextInt();
            for (int i = 1; i <= n; i++) head[i] = -1;
            idx = 0;
            for (int i = 1; i <= m; i++) {
                int u = sc.nextInt();
                int v = sc.nextInt();
                int c = sc.nextInt();
                if (c == 'x') add(u, v, 1);
                else add(u, v, c);
                add(v, u, c);
            }
            dij(s1);
            dij(s2);
            int ans = Math.abs(dis[t] - (n + 1));
            for (int i = 1; i <= m; i++) {
                int u = sc.nextInt();
                int v = sc.nextInt();
                int c = sc.nextInt();
                if (c == 'x') {
                    int x = get(u);
                    int y = get(v);
                    if (x != y) {
                        add(x, y, 1);
                        dij(s1);
                        dij(s2);
                        ans = Math.min(ans, Math.abs(dis[t] - (n + 1)));
                        add(x, y, 0);
                        dij(s1);
                        dij(s2);
                    }
                }
            }
            System.out.println(ans);
            if (n == 0 && m == 0 && s1 == 0 && s2 == 0 && t == 0) break;
        }
    }
}
