import sys
import heapq

def dijkstra(graph, start):
    distances = {node: float('inf') for node in graph}
    distances[start] = 0
    queue = [(0, start)]
    while queue:
        current_distance, current_node = heapq.heappop(queue)
        if current_distance > distances[current_node]:
            continue
        for neighbor, weight in graph[current_node].items():
            distance = current_distance + weight
            if distance < distances[neighbor]:
                distances[neighbor] = distance
                heapq.heappush(queue, (distance, neighbor))
    return distances

def solve():
    N, M, S1, S2, T = map(int, sys.stdin.readline().split())
    graph = {i: {} for i in range(1, N + 1)}
    for _ in range(M):
        a, b, w = sys.stdin.readline().split()
        a, b = int(a), int(b)
        if w == 'x':
            graph[a][b] = float('inf')
            graph[b][a] = float('inf')
        else:
            graph[a][b] = int(w)
            graph[b][a] = int(w)
    distances1 = dijkstra(graph, S1)
    distances2 = dijkstra(graph, S2)
    ans = abs(distances1[T] - distances2[T])
    for i in range(1, N + 1):
        for j in range(1, N + 1):
            if i != j and (i, j) in graph and graph[i][j] == float('inf'):
                for k in range(1, N + 1):
                    if k != i and k != j and (i, k) in graph and (k, j) in graph:
                        graph[i][j] = graph[i][k] + graph[k][j]
                        graph[j][i] = graph[j][k] + graph[k][i]
                distances1 = dijkstra(graph, S1)
                distances2 = dijkstra(graph, S2)
                ans = min(ans, abs(distances1[T] - distances2[T]))
    print(ans)

for _ in range(int(sys.stdin.readline())):
    solve()
