#include <iostream>
#include <vector>
#include <queue>
#include <bitset>
#include <cstring>
#include <algorithm>

using namespace std;

const int N = 35, M = 8, P = 1000000007;

int n, k, l[N], f[N], p[N], t[N], q[N], deg[N], cnt;
vector<int> e[N];
bitset<N> vis, col[N];
queue<int> q1, q2;

void dfs(int u) {
    vis[u] = 1;
    q1.push(u);
    for (int v : e[u]) {
        if (!vis[v]) {
            dfs(v);
        }
    }
}

void dfs2(int u, int c) {
    col[u] = c;
    q2.push(u);
    for (int v : e[u]) {
        if (col[v].none()) {
            dfs2(v, c);
        }
    }
}

int main() {
    int T;
    cin >> T;
    while (T--) {
        cin >> n >> k;
        for (int i = 1; i <= n; i++) {
            cin >> l[i];
            e[i].clear();
            deg[i] = 0;
        }
        for (int i = 1; i < n; i++) {
            cin >> f[i] >> p[i] >> t[i] >> q[i];
            e[f[i]].push_back(t[i]);
            e[t[i]].push_back(f[i]);
            deg[f[i]]++;
            deg[t[i]]++;
        }
        cnt = 0;
        vis.reset();
        for (int i = 1; i <= n; i++) {
            if (!vis[i]) {
                dfs(i);
                cnt++;
            }
        }
        long long ans = 0;
        for (int i = 0; i < (1 << cnt); i++) {
            for (int j = 1; j <= n; j++) {
                col[j].reset();
            }
            int sum = 0;
            for (int j = 1; j <= n; j++) {
                if (col[j].none()) {
                    dfs2(j, i >> (j - 1) & 1);
                    sum++;
                }
            }
            if (sum <= k) {
                int tmp = 1;
                for (int j = 1; j <= n; j++) {
                    if (col[j][0]) {
                        for (int v : e[j]) {
                            if (col[v][0]) {
                                tmp = (tmp * 2) % P;
                            }
                        }
                    }
                }
                ans = (ans + tmp) % P;
            }
        }
        cout << ans << endl;
    }
    return 0;
}
