import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static int n, k;
    static int[] l;
    static int[][] f;
    static int[][] p;
    static int[][] t;
    static int[][] q;
    static int[][] g;
    static int[][] h;
    static int[][] dp;
    static int[][] sum;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        int T = 1;
        while (T-- > 0) {
            StringTokenizer st = new StringTokenizer(br.readLine());
            n = Integer.parseInt(st.nextToken());
            k = Integer.parseInt(st.nextToken());
            l = new int[n];
            st = new StringTokenizer(br.readLine());
            for (int i = 0; i < n; i++) {
                l[i] = Integer.parseInt(st.nextToken());
            }
            f = new int[n - 1][4];
            p = new int[n - 1][4];
            t = new int[n - 1][4];
            q = new int[n - 1][4];
            for (int i = 0; i < n - 1; i++) {
                st = new StringTokenizer(br.readLine());
                for (int j = 0; j < 4; j++) {
                    f[i][j] = Integer.parseInt(st.nextToken());
                }
            }
            g = new int[n][n];
            h = new int[n][n];
            for (int i = 0; i < n - 1; i++) {
                int u = f[i][0];
                int v = f[i][3];
                int x = p[i][0];
                int y = q[i][1];
                g[u][v] = x;
                g[v][u] = x;
                h[u][v] = y;
                h[v][u] = y;
            }
            dp = new int[1 << n][k + 1];
            sum = new int[1 << n][k + 1];
            for (int i = 0; i < (1 << n); i++) {
                for (int j = 0; j <= k; j++) {
                    dp[i][j] = 0;
                    sum[i][j] = 0;
                }
            }
            dfs(0, 0);
            pw.println(dp[(1 << n) - 1][k]);
        }
        pw.close();
    }

    static void dfs(int u, int c) {
        if (u == n) {
            return;
        }
        for (int i = 0; i <= k; i++) {
            sum[u][i] = (sum[u][i] + dp[u][i]) % MOD;
        }
        for (int i = 0; i <= k; i++) {
            for (int j = 0; j <= k; j++) {
                if (i + j > k) {
                    break;
                }
                int v = 0;
                for (int x = 0; x < n; x++) {
                    if ((u >> x & 1) == 0) {
                        v |= (1 << x);
                    }
                }
                dp[u][i + j] = (dp[u][i + j] + (long) dp[v][i] * sum[u][j]) % MOD;
            }
        }
        for (int i = 0; i < n; i++) {
            if ((u >> i & 1) == 0) {
                int v = u | (1 << i);
                int x = g[i][u];
                int y = h[i][u];
                if (x == 0) {
                    continue;
                }
                int p = 0;
                for (int j = 0; j < n; j++) {
                    if ((v >> j & 1) == 0) {
                        p |= (1 << j);
                    }
                }
                dfs(v, c + 1);
                for (int j = 0; j <= k; j++) {
                    sum[u][j] = (sum[u][j] + (long) sum[v][j] * (k - j)) % MOD;
                }
                for (int j = 0; j <= k; j++) {
                    for (int l = 0; l <= k; l++) {
                        if (j + l > k) {
                            break;
                        }
                        int w = 0;
                        for (int z = 0; z < n; z++) {
                            if ((p >> z & 1) == 0) {
                                w |= (1 << z);
                            }
                        }
                        dp[u][j + l] = (dp[u][j + l] + (long) dp[p][j] * sum[v][l]) % MOD;
                        dp[u][j + l] = (dp[u][j + l] + (long) sum[p][j] * dp[v][l]) % MOD;
                    }
                }
            }
        }
    }
}
