#include <stdio.h>
#include <string.h>
#include <stdlib.h>

#define MAXN 100005

char str[3][MAXN];
int l, d;
int dp[MAXN][MAXN][MAXN];
char ans[MAXN];

void solve() {
    int i, j, k;
    for (i = 0; i <= l; i++) {
        for (j = 0; j <= l; j++) {
            for (k = 0; k <= l; k++) {
                dp[i][j][k] = -1;
            }
        }
    }
    dp[0][0][0] = 0;
    for (i = 0; i < l; i++) {
        for (j = 0; j <= l; j++) {
            for (k = 0; k <= l; k++) {
                if (dp[i][j][k] != -1) {
                    dp[i + 1][j + 1][k] = max(dp[i + 1][j + 1][k], dp[i][j][k] + (str[0][i] == str[1][j] && str[1][j] == str[2][k]));
                    dp[i + 1][j][k + 1] = max(dp[i + 1][j][k + 1], dp[i][j][k] + (str[0][i] == str[1][j] && str[1][j] != str[2][k]));
                    dp[i + 1][j + 1][k] = max(dp[i + 1][j + 1][k], dp[i][j][k] + (str[0][i] != str[1][j] && str[1][j] == str[2][k]));
                    dp[i + 1][j][k + 1] = max(dp[i + 1][j][k + 1], dp[i][j][k] + (str[0][i] != str[1][j] && str[1][j] != str[2][k]));
                }
            }
        }
    }
    int pos = 0;
    for (i = 0; i <= l; i++) {
        for (j = 0; j <= l; j++) {
            for (k = 0; k <= l; k++) {
                if (dp[i][j][k] == d) {
                    pos = i;
                    break;
                }
            }
            if (pos) break;
        }
        if (pos) break;
    }
    if (pos) {
        for (i = 0; i < pos; i++) {
            int min = 0;
            for (j = 0; j < 3; j++) {
                if (str[0][i] < str[1][i]) min = 0;
                else if (str[0][i] > str[1][i]) min = 1;
                else if (str[0][i] < str[2][i]) min = 0;
                else if (str[0][i] > str[2][i]) min = 2;
                else min = -1;
                if (min != -1) {
                    ans[i] = str[0][i];
                    break;
                }
            }
            if (min == -1) {
                for (j = 0; j < 3; j++) {
                    if (str[1][i] < str[2][i]) min = 1;
                    else if (str[1][i] > str[2][i]) min = 2;
                    else min = -1;
                    if (min != -1) {
                        ans[i] = str[1][i];
                        break;
                    }
                }
            }
        }
        for (i = 0; i < pos; i++) {
            printf("%c", ans[i]);
        }
        printf("\n");
    } else {
        printf("-1\n");
    }
}

int main() {
    int T;
    scanf("%d", &T);
    while (T--) {
        scanf("%d %d", &l, &d);
        for (int i = 0; i < 3; i++) {
            scanf("%s", str[i]);
        }
        solve();
    }
    return 0;
}
