#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int hammingDistance(const string& s1, const string& s2) {
    int count = 0;
    for (int i = 0; i < s1.size(); i++) {
        if (s1[i] != s2[i]) {
            count++;
        }
    }
    return count;
}

string lexicographicallyMinimum(const vector<string>& strings, int maxErrors) {
    string result = strings[0];
    for (int i = 1; i < strings.size(); i++) {
        if (hammingDistance(result, strings[i]) <= maxErrors) {
            result = min(result, strings[i]);
        }
    }
    return result;
}

int main() {
    int l, d;
    while (cin >> l >> d) {
        if (l == 0 && d == 0) {
            break;
        }
        vector<string> strings(3);
        for (int i = 0; i < 3; i++) {
            cin >> strings[i];
        }
        int minErrors = min({hammingDistance(strings[0], strings[1]), hammingDistance(strings[1], strings[2]), hammingDistance(strings[0], strings[2])});
        if (minErrors > d) {
            cout << -1 << endl;
        } else {
            cout << lexicographicallyMinimum(strings, d) << endl;
        }
    }
    return 0;
}
