#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int MAX_N = 100000;

struct Edge {
    int to, w;
};

vector<Edge> g[MAX_N];
int dist[MAX_N];

void dijkstra(int s) {
    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq;
    fill(dist, dist + MAX_N, 1e9);
    dist[s] = 0;
    pq.push({0, s});

    while (!pq.empty()) {
        int d = pq.top().first;
        int u = pq.top().second;
        pq.pop();

        if (d > dist[u]) continue;

        for (const auto& e : g[u]) {
            int v = e.to;
            int w = e.w;

            if (dist[v] > dist[u] + w) {
                dist[v] = dist[u] + w;
                pq.push({dist[v], v});
            }
        }
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, q;
    cin >> n >> q;

    for (int i = 0; i < n - 1; i++) {
        int u, v, w;
        cin >> u >> v >> w;
        u--; v--;

        g[u].push_back({v, w});
        g[v].push_back({u, w});
    }

    for (int i = 0; i < q; i++) {
        int a, b, c;
        cin >> a >> b >> c;
        a--; b--; c--;

        dijkstra(a);
        dijkstra(b);
        dijkstra(c);

        int ans = min({dist[a][b], dist[b][a], dist[a][c], dist[c][a], dist[b][c], dist[c][b]});
        cout << ans << '\n';

        fill(dist, dist + MAX_N, 1e9);
    }

    return 0;
}
