#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_M 100
#define MAX_N 100
#define MAX_K 100
#define MAX_S 100

int main() {
    int m, n;
    int k[MAX_M];
    int s[MAX_M][MAX_K];
    int cond[MAX_M][MAX_K];
    int t[MAX_M][MAX_K];
    int skills[MAX_N];

    scanf("%d %d", &m, &n);

    for (int i = 0; i < m; i++) {
        scanf("%d", &k[i]);
        for (int j = 0; j < k[i]; j++) {
            scanf("%d %s %d", &s[i][j], cond[i] + j, t[i] + j);
        }
    }

    for (int i = 0; i < n; i++) {
        skills[i] = 0;
    }

    for (int i = 0; i < m; i++) {
        int flag = 1;
        for (int j = 0; j < k[i]; j++) {
            if (strcmp(cond[i] + j, ">=") == 0) {
                if (skills[s[i][j] - 1] < t[i][j]) {
                    flag = 0;
                    break;
                }
            } else {
                if (skills[s[i][j] - 1] > t[i][j]) {
                    flag = 0;
                    break;
                }
            }
        }
        if (flag == 0) {
            printf("No\n");
            return 0;
        }
    }

    printf("Yes\n");

    return 0;
}
