import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int m = scanner.nextInt();
        int n = scanner.nextInt();
        int[][] conditions = new int[m][];
        for (int i = 0; i < m; i++) {
            int k = scanner.nextInt();
            conditions[i] = new int[k];
            for (int j = 0; j < k; j++) {
                conditions[i][j] = scanner.nextInt();
                String cond = scanner.next();
                int t = scanner.nextInt();
                if (cond.equals("<=")) {
                    conditions[i][j] = -t;
                }
            }
        }
        boolean[] canLearn = new boolean[m];
        for (int i = 0; i < m; i++) {
            int[] skills = conditions[i];
            int[] points = new int[n];
            for (int j = 0; j < skills.length; j++) {
                int skill = skills[j] - 1;
                String cond = scanner.next();
                int t = scanner.nextInt();
                if (cond.equals("<=")) {
                    points[skill] = Math.min(points[skill], t);
                } else {
                    points[skill] = Math.max(points[skill], t);
                }
            }
            canLearn[i] = true;
            for (int j = 0; j < skills.length; j++) {
                int skill = skills[j] - 1;
                if (points[skill] < conditions[i][j]) {
                    canLearn[i] = false;
                    break;
                }
            }
        }
        boolean canLearnAll = true;
        for (boolean b : canLearn) {
            if (!b) {
                canLearnAll = false;
                break;
            }
        }
        System.out.println(canLearnAll ? "Yes" : "No");
    }
}
