import java.io.*;
import java.util.*;

public class Main {
    static int N, Q;
    static int[] fa;
    static int[] dep;
    static int[][] dis;
    static List<int[]>[] g;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        N = Integer.parseInt(st.nextToken());
        Q = Integer.parseInt(st.nextToken());

        fa = new int[N + 1];
        dep = new int[N + 1];
        dis = new int[N + 1][N + 1];
        g = new List[N + 1];

        for (int i = 1; i <= N; i++) {
            g[i] = new ArrayList<>();
            fa[i] = i;
            dep[i] = 0;
            Arrays.fill(dis[i], Integer.MAX_VALUE);
        }

        for (int i = 1; i < N; i++) {
            st = new StringTokenizer(br.readLine());
            int u = Integer.parseInt(st.nextToken());
            int v = Integer.parseInt(st.nextToken());
            int w = Integer.parseInt(st.nextToken());
            g[u].add(new int[]{v, w});
            g[v].add(new int[]{u, w});
        }

        for (int i = 1; i <= N; i++) {
            dis[i][i] = 0;
        }

        for (int i = 1; i <= N; i++) {
            for (int j = 1; j <= N; j++) {
                dis[i][j] = dis[i][j - 1];
            }
        }

        for (int i = 1; i <= N; i++) {
            for (int[] e : g[i]) {
                int j = e[0];
                int w = e[1];
                dis[i][j] = Math.min(dis[i][j], w);
                dis[j][i] = Math.min(dis[j][i], w);
            }
        }

        for (int i = 1; i <= N; i++) {
            for (int j = 1; j <= N; j++) {
                for (int k = 1; k <= N; k++) {
                    dis[j][k] = Math.min(dis[j][k], dis[j][i] + dis[i][k]);
                }
            }
        }

        for (int i = 1; i <= Q; i++) {
            st = new StringTokenizer(br.readLine());
            int a = Integer.parseInt(st.nextToken());
            int b = Integer.parseInt(st.nextToken());
            int c = Integer.parseInt(st.nextToken());
            int ans = Math.min(Math.min(dis[a][b], dis[a][c]), Math.min(dis[b][c], dis[a][c]));
            pw.println(ans);
        }

        pw.close();
    }
}
