import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int[] nobleCards = new int[12];
        int[] frogCards = new int[2];
        int[] kappaCards = new int[2];
        int[] weaselCards = new int[2];

        for (int i = 0; i < 12; i++) {
            nobleCards[i] = scanner.nextInt();
        }

        for (int i = 0; i < 2; i++) {
            frogCards[i] = scanner.nextInt();
        }

        for (int i = 0; i < 2; i++) {
            kappaCards[i] = scanner.nextInt();
        }

        for (int i = 0; i < 2; i++) {
            weaselCards[i] = scanner.nextInt();
        }

        int[] scores = new int[3];
        Arrays.fill(scores, 0);

        int turn = 0;
        int[] players = {0, 1, 2};
        int[] playerTurn = {0, 1, 2};

        while (true) {
            int currentPlayer = players[turn % 3];
            int player = playerTurn[currentPlayer];

            for (int i = 0; i < 2; i++) {
                if (player == 0) {
                    if (frogCards[i] <= nobleCards.length) {
                        nobleCards = shiftArray(nobleCards, frogCards[i] - 1);
                        scores[0] += nobleCards[nobleCards.length - 1];
                        frogCards[i] = -1;
                    }
                } else if (player == 1) {
                    if (kappaCards[i] <= nobleCards.length) {
                        nobleCards = shiftArray(nobleCards, kappaCards[i] - 1);
                        scores[1] += nobleCards[nobleCards.length - 1];
                        kappaCards[i] = -1;
                    }
                } else {
                    if (weaselCards[i] <= nobleCards.length) {
                        nobleCards = shiftArray(nobleCards, weaselCards[i] - 1);
                        scores[2] += nobleCards[nobleCards.length - 1];
                        weaselCards[i] = -1;
                    }
                }
            }

            if (nobleCards[nobleCards.length - 1] == 0) {
                break;
            }

            nobleCards = shiftArray(nobleCards, nobleCards.length - 1);
            scores[0] += nobleCards[nobleCards.length - 1];
            scores[1] += nobleCards[nobleCards.length - 1];
            scores[2] += nobleCards[nobleCards.length - 1];

            turn++;
        }

        System.out.println(scores[0] + " " + scores[1] + " " + scores[2]);
    }

    public static int[] shiftArray(int[] array, int index) {
        int[] newArray = new int[array.length];
        System.arraycopy(array, 0, newArray, 0, index);
        System.arraycopy(array, index, newArray, index + 1, array.length - index - 1);
        return newArray;
    }
}
