#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define N 3005
#define M 30005

int n, m, k;
int head[N], edge[M], next[M], to[M], vis[N], cnt;
int dfn[N], low[N], sccno[N], scccnt, stack[N], top;
int in[N], out[N], scc[N];

void add(int u, int v) {
    edge[cnt] = v;
    next[cnt] = head[u];
    head[u] = cnt++;
}

void tarjan(int u) {
    dfn[u] = low[u] = ++scccnt;
    stack[++top] = u;
    vis[u] = 1;
    for (int i = head[u]; i != -1; i = next[i]) {
        int v = edge[i];
        if (!dfn[v]) {
            tarjan(v);
            low[u] = (low[u] < low[v]) ? low[u] : low[v];
        } else if (vis[v]) {
            low[u] = (low[u] < dfn[v]) ? low[u] : dfn[v];
        }
    }
    if (dfn[u] == low[u]) {
        sccno[u] = ++scccnt;
        while (stack[top] != u) {
            sccno[stack[top]] = scccnt;
            vis[stack[top]] = 0;
            top--;
        }
        vis[u] = 0;
        top--;
    }
}

int main() {
    scanf("%d %d %d", &n, &m, &k);
    memset(head, -1, n * sizeof *head);
    for (int i = 0; i < m; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        add(u, v);
        add(v, u);
    }
    for (int i = 1; i <= n; i++) {
        if (!dfn[i]) {
            tarjan(i);
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = head[i]; j != -1; j = next[j]) {
            int v = edge[j];
            if (sccno[i] != sccno[v]) {
                in[sccno[v]]++;
                out[sccno[i]]++;
            }
        }
    }
    int ans = 0;
    for (int i = 1; i <= scccnt; i++) {
        if (in[i] + out[i] + 1 > 2 * k) {
            printf("Impossible\n");
            return 0;
        }
        ans += (in[i] + out[i] + 1) / 2;
    }
    printf("%d\n", ans);
    return 0;
}
