#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int MAX_N = 3005;

int n, m, k;
vector<int> g[MAX_N];
int color[MAX_N];
int d[MAX_N];
int q[MAX_N];
int cnt;

void bfs(int s) {
    for (int i = 1; i <= n; i++) {
        d[i] = 1e9;
    }
    d[s] = 0;
    int h = 0;
    int t = 0;
    q[t++] = s;
    while (h < t) {
        int u = q[h++];
        for (int v : g[u]) {
            if (d[v] > d[u] + 1) {
                d[v] = d[u] + 1;
                if (color[v] == -1) {
                    q[t++] = v;
                }
            }
        }
    }
}

int main() {
    cin >> n >> m >> k;
    for (int i = 1; i <= m; i++) {
        int u, v;
        cin >> u >> v;
        g[u].push_back(v);
        g[v].push_back(u);
    }
    for (int i = 1; i <= n; i++) {
        color[i] = -1;
    }
    for (int i = 1; i <= n; i++) {
        if (color[i] == -1) {
            cnt = 0;
            bfs(i);
            for (int j = 1; j <= n; j++) {
                if (d[j] != 1e9) {
                    color[j] = 0;
                }
            }
            for (int j = 1; j <= n; j++) {
                if (color[j] == -1) {
                    color[j] = 1;
                    cnt++;
                }
            }
            if (cnt > k) {
                cout << "Impossible" << endl;
                return 0;
            }
        }
    }
    cout << k - cnt + 1 << endl;
    return 0;
}
