import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    static int N, M, K;
    static List<Integer>[] adj;
    static int[] color;
    static int[] low;
    static int[] disc;
    static int time;
    static int[] parent;
    static int[] q;
    static int[] vis;
    static int[] d;
    static int[] p;
    static int[] h;
    static int[] tin;
    static int[] tout;
    static int[] up;
    static int[] scc;
    static int[] cnt;
    static int[] dp;
    static int[] ans;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        M = Integer.parseInt(st.nextToken());
        K = Integer.parseInt(st.nextToken());
        adj = new List[N + 1];
        for (int i = 0; i <= N; i++) {
            adj[i] = new ArrayList<>();
        }
        for (int i = 0; i < M; i++) {
            st = new StringTokenizer(br.readLine());
            int u = Integer.parseInt(st.nextToken());
            int v = Integer.parseInt(st.nextToken());
            adj[u].add(v);
            adj[v].add(u);
        }
        color = new int[N + 1];
        low = new int[N + 1];
        disc = new int[N + 1];
        parent = new int[N + 1];
        q = new int[N + 1];
        vis = new int[N + 1];
        d = new int[N + 1];
        p = new int[N + 1];
        h = new int[N + 1];
        tin = new int[N + 1];
        tout = new int[N + 1];
        up = new int[N + 1];
        scc = new int[N + 1];
        cnt = new int[N + 1];
        dp = new int[N + 1];
        ans = new int[N + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        Arrays.fill(ans, Integer.MAX_VALUE);
        for (int i = 1; i <= N; i++) {
            if (color[i] == 0) {
                tarjan(i);
            }
        }
        for (int i = 1; i <= N; i++) {
            for (int j : adj[i]) {
                if (scc[i] != scc[j]) {
                    dp[scc[i]] = Math.max(dp[scc[i]], 1 + dp[scc[j]]);
                }
            }
        }
        for (int i = 1; i <= N; i++) {
            if (dp[scc[i]] <= K) {
                ans[scc[i]] = dp[scc[i]];
            }
        }
        int res = Integer.MAX_VALUE;
        for (int i = 1; i <= N; i++) {
            res = Math.min(res, ans[i]);
        }
        if (res == Integer.MAX_VALUE) {
            pw.println("Impossible");
        } else {
            pw.println(res);
        }
        pw.close();
    }

    static void tarjan(int u) {
        color[u] = 1;
        low[u] = disc[u] = time++;
        q[++top] = u;
        vis[u] = 1;
        for (int v : adj[u]) {
            if (color[v] == 0) {
                parent[v] = u;
                tarjan(v);
                low[u] = Math.min(low[u], low[v]);
            } else if (vis[v] == 1) {
                low[u] = Math.min(low[u], disc[v]);
            }
        }
        if (low[u] == disc[u]) {
            int v;
            do {
                v = q[top--];
                vis[v] = 0;
                scc[v] = id;
                tin[v] = time;
                tout[v] = time;
                time++;
                up[v] = u;
            } while (v != u);
            id++;
        }
    }
}
