#include <iostream>
#include <vector>
#include <algorithm>
#include <queue>
#include <functional>

using namespace std;

struct Edge {
    int u, v, w;
    bool operator<(const Edge& other) const {
        return w < other.w;
    }
};

int find(int x, vector<int>& parent) {
    if (parent[x] != x) {
        parent[x] = find(parent[x], parent);
    }
    return parent[x];
}

void unionSet(int x, int y, vector<int>& parent, vector<int>& size) {
    int rootX = find(x, parent);
    int rootY = find(y, parent);

    if (rootX != rootY) {
        if (size[rootX] < size[rootY]) {
            swap(rootX, rootY);
        }
        parent[rootY] = rootX;
        size[rootX] += size[rootY];
    }
}

int main() {
    int n, m;
    cin >> n >> m;

    vector<Edge> edges(m);
    for (int i = 0; i < m; ++i) {
        cin >> edges[i].u >> edges[i].v >> edges[i].w;
        --edges[i].u;
        --edges[i].v;
    }

    sort(edges.begin(), edges.end());

    vector<int> parent(n);
    iota(parent.begin(), parent.end(), 0);
    vector<int> size(n, 1);

    vector<int> mstCost(m);
    int mstCostSoFar = 0;
    for (int i = 0; i < m; ++i) {
        int u = edges[i].u;
        int v = edges[i].v;
        int w = edges[i].w;

        if (find(u, parent) != find(v, parent)) {
            mstCostSoFar += w;
            unionSet(u, v, parent, size);
        } else {
            mstCost[i] = mstCostSoFar;
        }
    }

    for (int i = m - 2; i >= 0; --i) {
        int u = edges[i].u;
        int v = edges[i].v;
        int w = edges[i].w;

        if (find(u, parent) != find(v, parent)) {
            mstCost[i] = mstCostSoFar;
        } else {
            mstCost[i] = mstCostSoFar - w;
        }
    }

    for (int i = 0; i < m; ++i) {
        if (mstCost[i] == 0) {
            cout << -1 << '\n';
        } else {
            cout << mstCost[i] << '\n';
        }
    }

    return 0;
}
