import sys
import heapq

def find(x):
    if parent[x] != x:
        parent[x] = find(parent[x])
    return parent[x]

def union(x, y):
    rootx = find(x)
    rooty = find(y)
    if rootx != rooty:
        if rank[rootx] > rank[rooty]:
            parent[rooty] = rootx
        else:
            parent[rootx] = rooty
            if rank[rootx] == rank[rooty]:
                rank[rooty] += 1

def kruskal(n, edges):
    global parent, rank
    parent = [i for i in range(n + 1)]
    rank = [0] * (n + 1)
    mst = []
    for _ in range(n - 1):
        e = heapq.heappop(edges)
        if find(e[0]) != find(e[1]):
            mst.append(e)
            union(e[0], e[1])
    return mst

def main():
    input = sys.stdin.readline
    n, m = map(int, input().split())
    edges = []
    for _ in range(m):
        a, b, w = map(int, input().split())
        edges.append((a, b, w))
    edges.sort(key=lambda x: x[2])
    mst = kruskal(n, edges)
    ans = [0] * m
    for i in range(m):
        e = edges[i]
        new_edges = edges[:i] + edges[i + 1:]
        new_mst = kruskal(n, new_edges)
        if len(new_mst) < n - 1:
            ans[i] = -1
        else:
            cost = 0
            for edge in new_mst:
                cost += edge[2]
            ans[i] = cost
    print('\n'.join(map(str, ans)))

if __name__ == '__main__':
    main()
