#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 1024
#define MAX 10000

typedef struct {
    double d;
    int c;
} pair;

int compare_pairs(const void *a, const void *b) {
    pair *p1 = (pair *)a;
    pair *p2 = (pair *)b;
    return (int)(p1->d - p2->d);
}

int main() {
    int n, i, j, x, y, count = 0;
    pair pairs[MAX];
    double sum = 0.0;
    int grid[N][N];

    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++) {
            scanf("%d", &grid[i][j]);
            count += grid[i][j];
        }
    }

    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++) {
            for (x = 0; x < n; x++) {
                for (y = 0; y < n; y++) {
                    double d = pow(i - x, 2) + pow(j - y, 2);
                    pairs[count++] = (pair){d, grid[i][j] * grid[x][y]};
                }
            }
        }
    }

    for (i = 0; i < count; i++) {
        sum += pairs[i].d * pairs[i].c;
    }

    double average = sum / (count * (count - 1));
    printf("%.10f\n", sqrt(average));

    qsort(pairs, count, sizeof(pair), compare_pairs);
    for (i = 0; i < count && i < MAX; i++) {
        printf("%.10f %d\n", sqrt(pairs[i].d), pairs[i].c);
    }

    return 0;
}
