#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>
#include <iomanip>

using namespace std;

int main() {
    int N;
    cin >> N;

    vector<vector<int>> grid(N, vector<int>(N));
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            cin >> grid[i][j];
        }
    }

    vector<int> total(N * N);
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            for (int k = 0; k < grid[i][j]; k++) {
                total[i * N + j]++;
            }
        }
    }

    vector<pair<double, int>> distances;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            for (int x = 0; x < N; x++) {
                for (int y = 0; y < N; y++) {
                    if (i != x || j != y) {
                        double dist = pow(i - x, 2) + pow(j - y, 2);
                        distances.push_back(make_pair(dist, total[i * N + j] * total[x * N + y]));
                    }
                }
            }
        }
    }

    sort(distances.begin(), distances.end());

    double sum = 0;
    for (int i = 0; i < distances.size(); i++) {
        sum += distances[i].first * distances[i].second;
    }

    double avg = sum / (double)distances.size();
    cout << fixed << setprecision(15) << sqrt(avg) << endl;

    int count = 0;
    for (int i = 0; i < distances.size(); i++) {
        if (count < 10000) {
            cout << fixed << setprecision(15) << sqrt(distances[i].first) << " " << distances[i].second << endl;
            count++;
        } else {
            break;
        }
    }

    return 0;
}
