import java.util.*;
import java.io.*;

public class Main {
    static class Pair implements Comparable<Pair> {
        double first;
        int second;

        public Pair(double first, int second) {
            this.first = first;
            this.second = second;
        }

        @Override
        public int compareTo(Pair o) {
            return Double.compare(this.first, o.first);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int N = Integer.parseInt(br.readLine());
        int[][] C = new int[N][N];
        for (int i = 0; i < N; i++) {
            StringTokenizer st = new StringTokenizer(br.readLine());
            for (int j = 0; j < N; j++) {
                C[i][j] = Integer.parseInt(st.nextToken());
            }
        }

        double sum = 0;
        List<Pair> dist = new ArrayList<>();
        for (int i = 0; i < N; i++) {
            for (int j = 0; j < N; j++) {
                for (int k = 0; k < N; k++) {
                    for (int l = 0; l < N; l++) {
                        if (C[i][j] > 0 && C[k][l] > 0) {
                            double d = Math.sqrt(Math.pow(i - k, 2) + Math.pow(j - l, 2));
                            sum += d;
                            dist.add(new Pair(d * d, 1));
                        }
                    }
                }
            }
        }

        double ave = sum / dist.size();
        Collections.sort(dist);
        int count = 0;
        for (Pair p : dist) {
            if (count == 10000) break;
            System.out.printf("%.10f %d%n", p.first, p.second);
            count++;
        }
        System.out.printf("%.10f%n", ave);
    }
}
