import math
import sys

def calculate_distance(x1, y1, x2, y2):
    return math.sqrt((x1 - x2) ** 2 + (y1 - y2) ** 2)

def main():
    N = int(input())
    grid = []
    for _ in range(N):
        grid.append(list(map(int, input().split())))

    total_distance = 0
    total_pairs = 0
    distances = {}
    for i in range(N):
        for j in range(N):
            for k in range(i, N):
                for col in range(j, N):
                    if i == k and j == col:
                        continue
                    distance = int(calculate_distance(i, j, k, col) * 10000)
                    total_distance += distance
                    total_pairs += 1
                    distances[distance] = distances.get(distance, 0) + 1

    average_distance = total_distance / total_pairs
    print(f"{average_distance:.10f}")
    for distance, count in sorted(distances.items()):
        print(f"{distance / 10000:.10f} {count}")

if __name__ == "__main__":
    main()
