#include <iostream>
#include <cmath>

int main() {
    double cx1, cy1, cz1, cx2, cy2, cz2;
    double vx11, vy11, vz11, vx12, vy12, vz12;
    double vx21, vy21, vz21, vx22, vy22, vz22;

    std::cin >> cx1 >> cy1 >> cz1;
    std::cin >> vx11 >> vy11 >> vz11 >> vx12 >> vy12 >> vz12;
    std::cin >> cx2 >> cy2 >> cz2;
    std::cin >> vx21 >> vy21 >> vz21 >> vx22 >> vy22 >> vz22;

    // Calculate vectors from center to circumference
    double v1x = vx11 * vx12 + vy11 * vy12 + vz11 * vz12;
    double v1y = vx11 * vy12 - vx12 * vy11 + vz11 * vz12;
    double v1z = vx11 * vz12 - vx12 * vz11 - vy11 * vy12;

    double v2x = vx21 * vx22 + vy21 * vy22 + vz21 * vz22;
    double v2y = vx21 * vy22 - vx22 * vy21 + vz21 * vz22;
    double v2z = vx21 * vz22 - vx22 * vz21 - vy21 * vy22;

    // Calculate cross product of two vectors
    double crossx = v1y * v2z - v1z * v2y;
    double crossy = v1z * v2x - v1x * v2z;
    double crossz = v1x * v2y - v1y * v2x;

    // Check if two circles are connected as chained rings
    if (std::abs(crossx) < 1e-9 && std::abs(crossy) < 1e-9 && std::abs(crossz) < 1e-9) {
        std::cout << "YES" << std::endl;
    } else {
        std::cout << "NO" << std::endl;
    }

    return 0;
}
