#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define N 100000
#define M 100000
#define Q 100000

typedef struct {
    int a, b, v;
} Query;

typedef struct {
    int *data;
    int size;
} SegmentTree;

SegmentTree *newSegmentTree(int n) {
    SegmentTree *st = (SegmentTree *)malloc(sizeof(SegmentTree));
    st->data = (int *)malloc((n << 2) * sizeof(int));
    st->size = n;
    return st;
}

void update(SegmentTree *st, int i, int v) {
    i += st->size;
    st->data[i] = v;
    while (i > 1) {
        i >>= 1;
        st->data[i] = st->data[i << 1] + st->data[i << 1 | 1];
    }
}

int query(SegmentTree *st, int l, int r) {
    int res = 0;
    l += st->size;
    r += st->size;
    while (l < r) {
        if (l & 1) {
            res += st->data[l];
            l++;
        }
        if (r & 1) {
            res += st->data[r - 1];
            r--;
        }
        l >>= 1;
        r >>= 1;
    }
    return res;
}

int main() {
    int n, m, q;
    scanf("%d %d %d", &n, &m, &q);
    Query queries[m];
    for (int i = 0; i < m; i++) {
        scanf("%d %d %d", &queries[i].a, &queries[i].b, &queries[i].v);
    }
    SegmentTree *st = newSegmentTree(n);
    for (int i = 0; i < m; i++) {
        update(st, queries[i].a - 1, queries[i].v);
        update(st, queries[i].b - 1, queries[i].v);
    }
    for (int i = 0; i < q; i++) {
        int x, y, j;
        scanf("%d %d %d", &x, &y, &j);
        int sum = query(st, x - 1, y - 1);
        int k = 0;
        for (int i = x - 1; i < y - 1; i++) {
            k += st->data[i + 1];
        }
        int l = 0, r = sum;
        while (l < r) {
            int mid = (l + r) / 2;
            int pos = query(st, 0, mid);
            if (pos < j) {
                l = mid + 1;
            } else {
                r = mid;
            }
        }
        printf("%d\n", query(st, l, r));
    }
    return 0;
}
