import sys
import bisect

class FenwickTree:
    def __init__(self, n):
        self.n = n
        self.tree = [0] * (n + 1)

    def update(self, i, delta):
        while i <= self.n:
            self.tree[i] += delta
            i += i & -i

    def query(self, i):
        res = 0
        while i > 0:
            res += self.tree[i]
            i -= i & -i
        return res

class SegmentTree:
    def __init__(self, n):
        self.n = n
        self.tree = [[0, 0] for _ in range(4 * n)]

    def build(self, node, l, r):
        if l == r:
            self.tree[node] = [l, 0]
            return
        mid = (l + r) // 2
        self.build(2 * node, l, mid)
        self.build(2 * node + 1, mid + 1, r)
        self.tree[node] = [self.tree[2 * node][0], self.tree[2 * node + 1][1]]

    def update(self, node, l, r, i, delta):
        if l == r:
            self.tree[node][1] += delta
            return
        mid = (l + r) // 2
        if i <= mid:
            self.update(2 * node, l, mid, i, delta)
        else:
            self.update(2 * node + 1, mid + 1, r, i, delta)
        self.tree[node] = [self.tree[2 * node][0], self.tree[2 * node + 1][1]]

    def query(self, node, l, r, x, y):
        if x <= l and r <= y:
            return self.tree[node]
        mid = (l + r) // 2
        if y <= mid:
            return self.query(2 * node, l, mid, x, y)
        if x > mid:
            return self.query(2 * node + 1, mid + 1, r, x, y)
        return [self.query(2 * node, l, mid, x, y)[0], self.query(2 * node + 1, mid + 1, r, x, y)[1]]

