#include <stdio.h>
#include <stdlib.h>

#define N 510
#define M 3005

int n, m;
int head[N], to[M], nxt[M], val[M], tot;
int dfn[N], low[N], scc[N], cnt, st[N], top;
int scc_cnt, scc_head[N], scc_tail[N], scc_size[N];

void add(int u, int v, int w) {
    to[tot] = v;
    val[tot] = w;
    nxt[tot] = head[u];
    head[u] = tot++;
}

void tarjan(int u) {
    dfn[u] = low[u] = ++cnt;
    st[++top] = u;
    for (int i = head[u]; i != -1; i = nxt[i]) {
        int v = to[i];
        if (!dfn[v]) {
            tarjan(v);
            low[u] = (low[u] < low[v]) ? low[u] : low[v];
        } else if (!scc[v]) {
            low[u] = (low[u] < dfn[v]) ? low[u] : dfn[v];
        }
    }
    if (dfn[u] == low[u]) {
        scc_cnt++;
        scc[u] = scc_cnt;
        while (1) {
            int v = st[top--];
            scc[v] = scc_cnt;
            scc_size[scc_cnt]++;
            if (u == v) break;
        }
    }
}

int main() {
    int i, j, k, x, y, z;
    while (scanf("%d%d", &n, &m) == 2) {
        for (i = 1; i <= n; i++) {
            head[i] = -1;
            dfn[i] = low[i] = scc[i] = 0;
        }
        tot = 0;
        for (i = 1; i <= m; i++) {
            scanf("%d%d", &x, &y);
            add(x, y, 1);
        }
        for (i = 1; i <= n; i++) {
            if (!dfn[i]) tarjan(i);
        }
        for (i = 1; i <= n; i++) {
            for (j = head[i]; j != -1; j = nxt[j]) {
                int v = to[j];
                if (scc[i] != scc[v]) {
                    add(scc[i], scc[v], val[j]);
                }
            }
        }
        for (i = 1; i <= scc_cnt; i++) {
            scc_head[i] = -1;
            scc_tail[i] = -1;
        }
        for (i = 1; i <= scc_cnt; i++) {
            for (j = head[i]; j != -1; j = nxt[j]) {
                int v = to[j];
                if (scc[i] != scc[v]) {
                    scc_tail[scc[i]] = j;
                    scc_head[scc[v]] = j;
                }
            }
        }
        int ans = 0;
        for (i = 1; i <= scc_cnt; i++) {
            if (scc_size[i] == 1) continue;
            int j = scc_head[i];
            while (j != -1) {
                int v = to[j];
                if (scc[i] != scc[v]) {
                    ans++;
                    j = scc_tail[scc[v]];
                } else {
                    j = nxt[j];
                }
            }
        }
        printf("%d\n", ans);
    }
    return 0;
}
