#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int N = 505;

int n, m;
vector<int> e[N];
int dfn[N], low[N], idx, scc[N], cnt;
bool ins[N];
queue<int> q;

void tarjan(int u) {
    dfn[u] = low[u] = ++idx;
    ins[u] = true;
    q.push(u);
    for (int v : e[u]) {
        if (!dfn[v]) {
            tarjan(v);
            low[u] = min(low[u], low[v]);
        } else if (ins[v]) {
            low[u] = min(low[u], dfn[v]);
        }
    }
    if (dfn[u] == low[u]) {
        ++cnt;
        while (true) {
            int v = q.front();
            q.pop();
            ins[v] = false;
            scc[v] = cnt;
            if (v == u) break;
        }
    }
}

int main() {
    int T;
    cin >> T;
    while (T--) {
        cin >> n >> m;
        for (int i = 1; i <= n; i++) {
            e[i].clear();
            dfn[i] = low[i] = 0;
            ins[i] = false;
            scc[i] = 0;
        }
        idx = cnt = 0;
        for (int i = 1; i <= m; i++) {
            int u, v;
            cin >> u >> v;
            e[u].push_back(v);
        }
        for (int i = 1; i <= n; i++) {
            if (!dfn[i]) tarjan(i);
        }
        int ans = n;
        for (int i = 1; i <= n; i++) {
            for (int j : e[i]) {
                if (scc[i] != scc[j]) {
                    ans--;
                    break;
                }
            }
        }
        cout << ans << endl;
    }
    return 0;
}
