import java.io.*;
import java.util.*;

public class Main {
    static int N, M;
    static int[] head, nxt, to, val;
    static int idx;
    static int[] dfn, low, stk, tp;
    static int cnt;
    static boolean[] vis;
    static int ans;

    static void add(int u, int v) {
        nxt[idx] = head[u];
        to[idx] = v;
        head[u] = idx++;
    }

    static void tarjan(int u) {
        dfn[u] = low[u] = ++cnt;
        stk[++tp[0]] = u;
        vis[u] = true;
        for (int i = head[u]; i != -1; i = nxt[i]) {
            int v = to[i];
            if (dfn[v] == 0) {
                tarjan(v);
                low[u] = Math.min(low[u], low[v]);
            } else if (vis[v]) {
                low[u] = Math.min(low[u], dfn[v]);
            }
        }
        if (dfn[u] == low[u]) {
            int v;
            do {
                v = stk[tp[0]--];
                vis[v] = false;
                val[v] = 1;
            } while (v != u);
            ans++;
        }
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        PrintWriter pw = new PrintWriter(System.out);
        int T = 1;
        while (T-- > 0) {
            N = sc.nextInt();
            M = sc.nextInt();
            head = new int[N + 1];
            nxt = new int[3 * N + 1];
            to = new int[3 * N + 1];
            val = new int[N + 1];
            dfn = new int[N + 1];
            low = new int[N + 1];
            stk = new int[N + 1];
            tp = new int[1];
            vis = new boolean[N + 1];
            idx = 0;
            cnt = 0;
            ans = 0;
            for (int i = 1; i <= N; i++) {
                head[i] = -1;
                dfn[i] = 0;
                low[i] = 0;
                vis[i] = false;
            }
            for (int i = 1; i <= M; i++) {
                int u = sc.nextInt();
                int v = sc.nextInt();
                add(u, v);
            }
            for (int i = 1; i <= N; i++) {
                if (dfn[i] == 0) {
                    tarjan(i);
                }
            }
            pw.println(N - ans);
        }
        pw.close();
    }

    static class Scanner {
        BufferedReader br;
        StringTokenizer st;

        public Scanner(InputStream in) {
            br = new BufferedReader(new InputStreamReader(in));
        }

        public String next() throws IOException {
            while (st == null || !st.hasMoreTokens())
                st = new StringTokenizer(br.readLine());
            return st.nextToken();
        }

        public int nextInt() throws IOException {
            return Integer.parseInt(next());
        }
    }
}
