#include <iostream>
#include <vector>
#include <string>

using namespace std;

int main() {
    int t;
    cin >> t;
    while (t--) {
        int n;
        cin >> n;
        vector<int> a(n);
        for (int i = 0; i < n; i++) {
            string s;
            cin >> s;
            if (s == "x") {
                a[i] = -1;
            } else {
                a[i] = stoi(s);
            }
        }

        int left = 0;
        int right = 1000000000;
        bool found = false;
        while (left <= right) {
            int mid = (left + right) / 2;
            bool possible = true;
            for (int i = 0; i < n; i++) {
                if (a[i] == -1) {
                    if (i % 2 == 0) {
                        if (a[i - 1] <= mid && mid <= a[i + 1]) {
                            possible = false;
                            break;
                        }
                    } else {
                        if (a[i - 1] >= mid && mid >= a[i + 1]) {
                            possible = false;
                            break;
                        }
                    }
                }
            }
            if (possible) {
                found = true;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }

        if (found) {
            cout << left << endl;
        } else if (left == 0 && right == 1000000000) {
            cout << "none" << endl;
        } else {
            cout << "ambiguous" << endl;
        }
    }
    return 0;
}
