import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            if (n == 0) break;
            int[] a = new int[n];
            for (int i = 0; i < n; i++) {
                String s = scanner.next();
                if (s.equals("x")) a[i] = -1;
                else a[i] = Integer.parseInt(s);
            }
            boolean flag = true;
            for (int i = 1; i < n - 1; i++) {
                if (a[i] == -1) continue;
                if (a[i] > a[i - 1] && a[i] > a[i + 1]) flag = false;
                if (a[i] < a[i - 1] && a[i] < a[i + 1]) flag = false;
            }
            if (flag) {
                for (int i = 1; i < n - 1; i++) {
                    if (a[i] == -1) {
                        if (a[i - 1] > a[i + 1]) a[i] = a[i - 1];
                        else a[i] = a[i + 1];
                    }
                }
                int count = 0;
                for (int i = 1; i < n - 1; i++) {
                    if (a[i] == -1) count++;
                }
                if (count == 1) {
                    for (int i = 1; i < n - 1; i++) {
                        if (a[i] == -1) {
                            System.out.println(a[i + 1]);
                            break;
                        }
                    }
                } else if (count == 0) {
                    System.out.println("none");
                } else {
                    System.out.println("ambiguous");
                }
            } else {
                System.out.println("ambiguous");
            }
        }
    }
}
