#include <stdio.h>
#include <string.h>

#define MOD 1000000007

int dp[55][55][55][55];
int a[55], b[55], c[55];

void solve(int len) {
    for (int i = 0; i <= 9; i++) {
        for (int j = 0; j <= 9; j++) {
            for (int k = 0; k <= 9; k++) {
                for (int l = 0; l <= 9; l++) {
                    dp[i][j][k][l] = 0;
                }
            }
        }
    }
    dp[0][0][0][0] = 1;
    for (int i = 0; i < len; i++) {
        for (int x = 0; x <= 9; x++) {
            for (int y = 0; y <= 9; y++) {
                for (int z = 0; z <= 9; z++) {
                    for (int w = 0; w <= 9; w++) {
                        if (dp[x][y][z][w] == 0) continue;
                        for (int d = 0; d <= 9; d++) {
                            if (a[i] == '?' || a[i] == d) {
                                int nx = x * 10 + d;
                                if (nx <= 9 * 10) {
                                    dp[d][y][z][w] = (dp[d][y][z][w] + dp[x][y][z][w]) % MOD;
                                }
                            }
                            if (b[i] == '?' || b[i] == d) {
                                int ny = y * 10 + d;
                                if (ny <= 9 * 10) {
                                    dp[x][d][z][w] = (dp[x][d][z][w] + dp[x][y][z][w]) % MOD;
                                }
                            }
                            if (c[i] == '?' || c[i] == d) {
                                int nz = z * 10 + d;
                                if (nz <= 9 * 10) {
                                    dp[x][y][d][w] = (dp[x][y][d][w] + dp[x][y][z][w]) % MOD;
                                }
                            }
                            if (a[i] == '?' && b[i] == '?' && c[i] == '?') {
                                int nw = w * 10 + d;
                                if (nw <= 9 * 10) {
                                    dp[x][y][z][d] = (dp[x][y][z][d] + dp[x][y][z][w]) % MOD;
                                }
                            }
                        }
                    }
                }
            }
        }
    }
    int ans = 0;
    for (int i = 0; i <= 9; i++) {
        for (int j = 0; j <= 9; j++) {
            for (int k = 0; k <= 9; k++) {
                ans = (ans + dp[i][j][k][0]) % MOD;
            }
        }
    }
    printf("%d\n", ans);
}

int main() {
    while (1) {
        char str[55];
        scanf("%s", str);
        if (str[0] == '0') break;
        int len = strlen(str);
        for (int i = 0; i < len; i++) {
            if (str[i] == '?') {
                a[i] = b[i] = c[i] = '?';
            } else {
                a[i] = str[i] - '0';
                b[i] = str[i] - '0';
                c[i] = str[i] - '0';
            }
        }
        solve(len);
    }
    return 0;
}
