#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int MOD = 1e9 + 7;

int dp[55][55][55][55];
int n;

void solve() {
    cin >> n;
    if (n == 0) return;
    string a, b, c;
    cin >> a >> b >> c;
    int len = a.size();
    for (int i = 0; i <= len; i++) {
        for (int j = 0; j <= len; j++) {
            for (int k = 0; k <= len; k++) {
                for (int l = 0; l <= len; l++) {
                    dp[i][j][k][l] = 0;
                }
            }
        }
    }
    dp[0][0][0][0] = 1;
    for (int i = 0; i < len; i++) {
        for (int j = 0; j <= len; j++) {
            for (int k = 0; k <= len; k++) {
                for (int l = 0; l <= len; l++) {
                    if (dp[i][j][k][l] == 0) continue;
                    if (a[i] == '?') {
                        for (int x = 0; x <= 9; x++) {
                            if (x + j <= len && x + k <= len && x + l <= len) {
                                dp[i + 1][x + j][x + k][x + l] = (dp[i + 1][x + j][x + k][x + l] + dp[i][j][k][l]) % MOD;
                            }
                        }
                    } else {
                        if (a[i] - '0' + j <= len && a[i] - '0' + k <= len && a[i] - '0' + l <= len) {
                            dp[i + 1][a[i] - '0' + j][a[i] - '0' + k][a[i] - '0' + l] = (dp[i + 1][a[i] - '0' + j][a[i] - '0' + k][a[i] - '0' + l] + dp[i][j][k][l]) % MOD;
                        }
                    }
                    if (b[i] == '?') {
                        for (int x = 0; x <= 9; x++) {
                            if (x + j <= len && x + k <= len && x + l <= len) {
                                dp[i][j][x + k][x + l] = (dp[i][j][x + k][x + l] + dp[i][j][k][l]) % MOD;
                            }
                        }
                    } else {
                        if (b[i] - '0' + j <= len && b[i] - '0' + k <= len && b[i] - '0' + l <= len) {
                            dp[i][j][b[i] - '0' + k][b[i] - '0' + l] = (dp[i][j][b[i] - '0' + k][b[i] - '0' + l] + dp[i][j][k][l]) % MOD;
                        }
                    }
                    if (c[i] == '?') {
                        for (int x = 0; x <= 9; x++) {
                            if (x + j <= len && x + k <= len && x + l <= len) {
                                dp[i][j][k][x + l] = (dp[i][j][k][x + l] + dp[i][j][k][l]) % MOD;
                            }
                        }
                    } else {
                        if (c[i] - '0' + j <= len && c[i] - '0' + k <= len && c[i] - '0' + l <= len) {
                            dp[i][j][k][c[i] - '0' + l] = (dp[i][j][k][c[i] - '0' + l] + dp[i][j][k][l]) % MOD;
                        }
                    }
                }
            }
        }
    }
    int ans = 0;
    for (int i = 0; i <= len; i++) {
        for (int j = 0; j <= len; j++) {
            for (int k = 0; k <= len; k++) {
                ans = (ans + dp[len][i][j][k]) % MOD;
            }
        }
    }
    cout << ans << '\n';
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    while (true) {
        solve();
        string s;
        cin >> s;
        if (s == "0") break;
    }
    return 0;
}
