import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.StringTokenizer;

public class Main {
    static final int MOD = 1000000007;
    static final int MAXN = 55;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st;

        int[][] dp = new int[MAXN][MAXN];
        int[][] a = new int[MAXN][MAXN];
        int[][] b = new int[MAXN][MAXN];
        int[][] c = new int[MAXN][MAXN];

        while (true) {
            st = new StringTokenizer(br.readLine());
            String A = st.nextToken();
            String B = st.nextToken();
            String C = st.nextToken();

            if (C.equals("0")) break;

            int n = A.length();
            for (int i = 0; i < n; i++) {
                a[i][i] = A.charAt(i) - '0';
                b[i][i] = B.charAt(i) - '0';
                c[i][i] = C.charAt(i) - '0';
            }

            for (int i = 0; i < n; i++) {
                for (int j = i + 1; j < n; j++) {
                    if (A.charAt(j) == '?' && B.charAt(j) != '?') {
                        a[i][j] = b[i][j] = c[i][j] = b.charAt(j) - '0';
                    } else if (A.charAt(j) != '?' && B.charAt(j) == '?') {
                        a[i][j] = b[i][j] = c[i][j] = a.charAt(j) - '0';
                    } else if (A.charAt(j) == '?' && B.charAt(j) == '?') {
                        a[i][j] = b[i][j] = c[i][j] = (a.charAt(j) - '0') + (b.charAt(j) - '0');
                    } else {
                        a[i][j] = b[i][j] = c[i][j] = (a.charAt(j) - '0') + (b.charAt(j) - '0');
                    }
                }
            }

            for (int i = n - 2; i >= 0; i--) {
                for (int j = i + 1; j < n; j++) {
                    for (int k = i; k <= j; k++) {
                        if (a[i][j] == a[i][k] + a[k + 1][j] && b[i][j] == b[i][k] + b[k + 1][j] && c[i][j] == c[i][k] + c[k + 1][j]) {
                            dp[i][j] = (dp[i][j] + dp[i][k] * dp[k + 1][j]) % MOD;
                        }
                    }
                }
            }

            pw.println(dp[0][n - 1]);
        }

        pw.close();
    }
}
