MOD = 10**9 + 7

def count_assignments(A, B, C):
    n = len(A)
    dp = [[0] * 10 for _ in range(n + 1)]
    dp[0][int(A[0])] = 1
    dp[0][int(B[0])] = 1
    dp[0][int(C[0])] = 1

    for i in range(1, n):
        for a in range(10):
            for b in range(10):
                if a + b == int(C[i]):
                    for c in range(10):
                        if c != int(C[i]):
                            dp[i][c] += dp[i - 1][a]
                            dp[i][c] %= MOD
                        elif a != int(A[i]) and b != int(B[i]):
                            dp[i][c] += dp[i - 1][a]
                            dp[i][c] %= MOD

    return sum(dp[n]) % MOD

def main():
    while True:
        A = input().split()
        if A[0] == '0':
            break
        A = ['0' + a if len(a) == 1 else a for a in A]
        B = ['0' + b if len(b) == 1 else b for b in input().split()]
        C = ['0' + c if len(c) == 1 else c for c in input().split()]
        assert len(A) == len(B) == len(C)
        print(count_assignments(A[1], B[1], C[1]))

if __name__ == "__main__":
    main()
