#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int MAX_N = 305;
const int MAX_M = 5005;
const int MAX_L = 17;

int n, m, l, s, t;
vector<int> g[MAX_N];
vector<int> f[MAX_N];
int dist[MAX_N];
int e[MAX_L];
int dp[MAX_N][MAX_L];

void dijkstra(int s) {
    fill(dist, dist + n + 1, 1e9);
    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq;
    pq.push({0, s});
    dist[s] = 0;
    while (!pq.empty()) {
        int u = pq.top().second;
        int d = pq.top().first;
        pq.pop();
        if (d > dist[u]) continue;
        for (int v : g[u]) {
            if (dist[v] > dist[u] + g[u][v]) {
                dist[v] = dist[u] + g[u][v];
                pq.push({dist[v], v});
            }
        }
    }
}

int main() {
    while (cin >> n >> m >> l >> s >> t) {
        if (n == 0 && m == 0 && l == 0 && s == 0 && t == 0) break;
        for (int i = 1; i <= n; i++) {
            g[i].clear();
            f[i].clear();
            for (int j = 0; j <= l; j++) {
                dp[i][j] = 0;
            }
        }
        for (int i = 1; i <= m; i++) {
            int u, v, w;
            cin >> u >> v >> w;
            g[u].push_back(v);
            g[v].push_back(u);
            f[u].push_back(w);
            f[v].push_back(w);
        }
        for (int i = 1; i <= l; i++) {
            cin >> e[i];
        }
        dijkstra(s);
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= l; j++) {
                dp[i][j] = max(dp[i][j], dp[i][j - 1]);
                if (dist[i] + e[j] <= t) {
                    dp[i][j] = max(dp[i][j], dp[i][j - 1] + 1);
                }
            }
        }
        int ans = 0;
        for (int i = 1; i <= l; i++) {
            ans = max(ans, dp[s][i]);
        }
        cout << ans << endl;
    }
    return 0;
}
