import sys
from collections import deque

def dijkstra(graph, start):
    distances = {node: float('inf') for node in graph}
    distances[start] = 0
    queue = deque([start])
    while queue:
        node = queue.popleft()
        for neighbor, weight in graph[node].items():
            if distances[neighbor] > distances[node] + weight:
                distances[neighbor] = distances[node] + weight
                queue.append(neighbor)
    return distances

def solve():
    n, m, l, s, t = map(int, sys.stdin.readline().split())
    graph = {i: {} for i in range(1, n + 1)}
    for _ in range(m):
        u, v, w = map(int, sys.stdin.readline().split())
        graph[u][v] = w
        graph[v][u] = w
    siros = {}
    for _ in range(l):
        j, e = map(int, sys.stdin.readline().split())
        siros[j] = e
    distances = dijkstra(graph, s)
    max_eat = 0
    for siro in siros:
        if distances[siro] + siros[siro] <= t:
            max_eat += 1
    print(max_eat)

for _ in range(int(sys.stdin.readline())):
    solve()
