#include <iostream>
#include <vector>
#include <algorithm>
#include <utility>

using namespace std;

const int MOD = 1000000007;

int n, m;
vector<int> g[100005];
int dp[100005];
int parent[100005];
int size[100005];

void dfs(int u) {
    for (int v : g[u]) {
        if (v != parent[u]) {
            parent[v] = u;
            size[v] = 1;
            dfs(v);
            size[u] += size[v];
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    while (cin >> n >> m) {
        if (n == 0 && m == 0) break;

        for (int i = 0; i <= n; i++) {
            g[i].clear();
            parent[i] = i;
            size[i] = 1;
            dp[i] = 1;
        }

        for (int i = 0; i < m; i++) {
            int u, v;
            cin >> u >> v;
            g[u].push_back(v);
        }

        for (int i = 1; i <= n; i++) {
            dfs(i);
        }

        for (int i = 1; i <= n; i++) {
            for (int j : g[i]) {
                if (parent[i] == j) continue;
                dp[parent[i]] = (dp[parent[i]] + dp[i] * size[j]) % MOD;
            }
        }

        int ans = 0;
        for (int i = 1; i <= n; i++) {
            ans = (ans + dp[i]) % MOD;
        }

        cout << ans << '\n';
    }

    return 0;
}
