import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static int N, M;
    static int[][] G;
    static int[] fa;
    static int[] sz;
    static int[] dep;
    static int[] ans;
    static int[] vis;
    static int[] dfn;
    static int[] low;
    static int[] stk;
    static int top;
    static int[] scc;
    static int cnt;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        int T = 1;
        while (T-- > 0) {
            StringTokenizer st = new StringTokenizer(br.readLine());
            N = Integer.parseInt(st.nextToken());
            M = Integer.parseInt(st.nextToken());
            G = new int[N + 1][];
            for (int i = 1; i <= N; i++) {
                G[i] = new int[0];
            }
            for (int i = 1; i <= M; i++) {
                st = new StringTokenizer(br.readLine());
                int u = Integer.parseInt(st.nextToken());
                int v = Integer.parseInt(st.nextToken());
                G[u] = Arrays.copyOf(G[u], G[u].length + 1);
                G[u][G[u].length - 1] = v;
            }
            fa = new int[N + 1];
            sz = new int[N + 1];
            dep = new int[N + 1];
            ans = new int[N + 1];
            vis = new int[N + 1];
            dfn = new int[N + 1];
            low = new int[N + 1];
            stk = new int[N + 1];
            scc = new int[N + 1];
            cnt = 0;
            for (int i = 1; i <= N; i++) {
                if (vis[i] == 0) {
                    dfs(i, 0);
                }
            }
            for (int i = 1; i <= N; i++) {
                if (scc[i] == i) {
                    dfs2(i, 0);
                }
            }
            long res = 0;
            for (int i = 1; i <= N; i++) {
                res = (res + ans[i]) % MOD;
            }
            pw.println(res);
        }
        pw.close();
    }

    static void dfs(int u, int f) {
        vis[u] = 1;
        dfn[u] = low[u] = ++cnt;
        stk[++top] = u;
        for (int v : G[u]) {
            if (v == f) continue;
            if (vis[v] == 0) {
                dep[v] = dep[u] + 1;
                dfs(v, u);
                low[u] = Math.min(low[u], low[v]);
            } else {
                low[u] = Math.min(low[u], dfn[v]);
            }
        }
        if (dfn[u] == low[u]) {
            int v;
            do {
                v = stk[top--];
                scc[v] = u;
            } while (v != u);
        }
    }

    static void dfs2(int u, int f) {
        ans[scc[u]] = (int) ((ans[scc[u]] + 1L * (1 << (dep[u] - dep[scc[u]]))) % MOD);
        for (int v : G[u]) {
            if (v != f) {
                dfs2(v, u);
            }
        }
    }
}
